const initialState = {
    gnomes : [],
    gnome : null,
    gnomeDetailsLoading : true,
    listLoading : true,
} 

export default function(state=initialState, action){
    switch(action.type){
        case "FETCH_GNOMES":
            return {
                ...state,
                gnomes: action.payload,
                listLoading: false
            };
