import React from 'react';
import ListItem from './ListItem';
import './styles.css';
import { connect } from 'react-redux';
import {fetchGnomes} from '../actions/getActions'
import PropTypes from 'prop-types';


class ListView extends React.Component {
    // constructor(props){
    //     super(props);
    //     this.state = {
    //         gnomes:[]
    //     }
    // }
    componentWillMount(){
        this.props.fetchGnomes();
    }
    render(){
        return(
            <div className="gnomesList">
                {
                    this.props.gnomes.map((gnome) => (
                        <div className="item" key={gnome.id}>
                        < ListItem gnome={gnome}/>
                        </div>
                    ))
                }
            </div>
        );
    }
}

function mapStateToProps(state) {
    return {
        gnomes: state.gnomes
    };
}

function matchDispatchToProps(dispatch){
    return bindActionCreators({selectUser: selectUser}, dispatch);
}

export default connect(mapStateToProps, matchDispatchToProps)(ListView);
