import React from 'react';
import './styles.css';
import gnome from './gnome.png'
import {Link} from 'react-router-dom';
import { connect } from 'react-redux';
import {editHairFilters,addHairFilter,removeHairFilter } from '../actions/filtersActions'
import {fetchGnomesHairFilters } from '../actions/getActions'
import PropTypes from 'prop-types';


class Toolbar extends React.Component {

    expanded = false;

    showCheckboxes(id) {
      var checkboxes = document.getElementById("checkboxes"+id);
      if (!this.expanded) {
        checkboxes.style.display = "block";
        this.expanded = true;
      } else {
        checkboxes.style.display = "none";
        this.expanded = false;
      }
    }

    handleChange= (event) => {
      if(event.target.checked == true){
        this.props.addHairFilter(event.target.value)
      }else{
        this.props.removeHairFilter(event.target.value)

      }
    }




    componentDidMount(){
    }

/* Set the width of the side navigation to 250px */

  openNav() {
    document.getElementById("mySidenav").style.height = "10%";
    document.getElementById("gnomesList").style.marginTop  = "150px";

  }
  
  /* Set the width of the side navigation to 0 */
 closeNav() {
    document.getElementById("mySidenav").style.height = "0";
    document.getElementById("gnomesList").style.marginTop = "80px";
  }

    render(){
        return(
            <div>
            <div className="topnav">
            <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"></link>

            <Link to="/" className="link">
                <img src={gnome} className="logo" alt=""></img>
                <a>
                BrastlewApp
                </a> 
            </Link>


            <div className="search-container">
                <form action="/action_page.php">
                    <input type="text" placeholder="Search.." name="search"></input>
                    <button type="submit"><i className="fa fa-search"></i></button>
                </form>
            </div>
            <button className="openFilters" id="openButton" onClick={() => this.openNav()}>&#9776;</button>


            </div>
            <div id="mySidenav" class="sidenav">
                <a href="javascript:void(0)" class="closebtn" onClick={() => this.closeNav()}>&times;</a>

                      <div class="multiselect">
                        <div class="selectBox" onClick={()=>this.showCheckboxes(1)}>
                        <button>HAIR FILTERS</button>
                        <div class="overSelect"></div>
                        </div>
                        <div id="checkboxes1">
                          <label for="Pink">
                            <input type="checkbox" value="Pink" id="Pink" onChange={ this.handleChange }/>Pink</label>
                          <label for="Black">
                            <input type="checkbox" value="Black" id="Black" onChange={ this.handleChange }/>Black</label>
                          <label for="Red">
                            <input type="checkbox" value="Red" id="Red" onChange={ this.handleChange }/>Red</label>
                          <label for="Gray">
                            <input type="checkbox" value="Gray" id="Gray" onChange={ this.handleChange }/>Gray</label>
                          <label for="Green">
                            <input type="checkbox" value="Green" id="Green" onChange={ this.handleChange }/>Green</label>
                        </div>
                      </div>
                      <div class="multiselect">
                        <div class="selectBox2" onClick={()=>this.showCheckboxes(2)}>
                        <button>PROFESSIONS FILTERS</button>
                        <div class="overSelect"></div>
                        </div>
                        <div id="checkboxes2">
                          <label for="Pink">
                            <input type="checkbox" id="Pink" onChange={ this.handleChange }/>Pink</label>
                          <label for="Black">
                            <input type="checkbox" id="Black" onChange={ this.handleChange }/>Black</label>
                          <label for="Red">
                            <input type="checkbox" id="Red" onChange={ this.handleChange }/>Red</label>
                          <label for="Gray">
                            <input type="checkbox" id="Grey" onChange={ this.handleChange }/>Gray</label>
                          <label for="Green">
                            <input type="checkbox" id="Red" onChange={ this.handleChange }/>Green</label>
                        </div>
                      </div>


                      <button id="filtersButton" onClick={()=>this.props.fetchGnomesHairFilters(this.props.hairFilters,this.props.gnomes)}>Apply</button>
                  </div>
            </div>




        )
    }
}



Toolbar.propTypes = {
  editHairFilters: PropTypes.func.isRequired,
  addHairFilter: PropTypes.func.isRequired,
  removeHairFilter: PropTypes.func.isRequired,
  fetchGnomesHairFilters: PropTypes.func.isRequired,
  hairFilters: PropTypes.array.isRequired,
  gnomes: PropTypes.array.isRequired,
}

const mapStateToProps = state => ({
  hairFilters: state.filters.hairFilters,
  gnomes: state.gnomes.gnomes,
});

export default connect(mapStateToProps, { editHairFilters,addHairFilter,removeHairFilter,fetchGnomesHairFilters })(Toolbar);
