export function editHairFilters(filters){
    return function(dispatch){
            dispatch({
                type:"HAIR_FILTERS",
                payload: filters
            });
    }
}

export function addHairFilter(filters,filter){
    return function(dispatch){
        console.log(filters);
        console.log(filter);
        filters.push(filter);
        console.log(filters);

            dispatch({
                type:"HAIR_FILTERS_ADD",
                payload: filters
            });
    }
}

export function removeHairFilter(filters,filter){
    return function(dispatch){
            dispatch({
                type:"HAIR_FILTERS_REMOVE",
                payload: filter
            });
    }
}



